mapper.select:
SELECT FESTA_ID, FESTA_YEAR, FESTA_NAME, FESTA_ADDR, FESTA_DATE, FESTA_CELEB
FROM FESTIVAL
WHERE FESTA_ID = #{FESTA_ID}

mapper.update:
UPDATE FESTIVAL
SET
FESTA_YEAR=#{FESTA_YEAR}, FESTA_NAME=#{FESTA_NAME}, FESTA_ADDR=#{FESTA_ADDR}, FESTA_DATE=#{FESTA_DATE},
FESTA_CELEB=#{FESTA_CELEB}, FESTA_TICKET_DATE=#{FESTA_TICKET_DATE}, FESTA_TICKET_ADDR=#{FESTA_TICKET_ADDR}
WHERE FESTA_ID=#{FESTA_ID}
